<?phpnamespace app\components\censure;use yii\validators\Validator;use app\components\censure\text\Censure;class Filter extends Validator{	/**    * @param string $text the text to filtering.    * @return string    */			public static function applyFilter($string)	{		if (trim($string) == '') return '';							return Censure::parse($string, 3, "\xe2\x80\xa6", false, ' *** ', 'UTF-8');	}						}